{
    TAF_UINT16 count = 0;
    TAF_UINT8 *tmp   = data;

    if (*tmp == '+') {
        tmp++;
        count++;
    }

    while (count++ < len) {
        if ((*tmp >= '0') && (*tmp <= '9')) {
        } else if ((*tmp >= 'a') && (*tmp <= 'c')) {
        } else if ((*tmp >= 'A') && (*tmp <= 'C')) {
        } else if ((*tmp == '*') || (*tmp == '#')) {
        } else {
            return AT_FAILURE;
        }
        tmp++;
    }
    return AT_SUCCESS;
}


TAF_UINT32 At_CheckPBString(TAF_UINT8 *data, TAF_UINT16 *len)
{
    TAF_UINT16 count      = 0;
    TAF_UINT8 *tmp        = data;
    TAF_UINT8  invalidNum = 0;
    errno_t    returnValue;

    while (count++ < *len) {
        if ((*tmp == '(') || (*tmp == ')') || (*tmp == '-') || (*tmp == '"')) {
            if (count < *len) {
                returnValue = memmove_s(tmp, (VOS_SIZE_T)(*len - count), tmp + 1, (VOS_SIZE_T)(*len - count));
                TAF_MEM_CHK_RTN_VAL(returnValue, (VOS_SIZE_T)(*len - count), (VOS_SIZE_T)(*len - count));
            }
            invalidNum++;
        } else {
            tmp++;
        }
    }

    count = 0;
    tmp   = data;
    *len -= invalidNum;

    while (count++ < *len) {
        if (!(((*tmp >= '0') && (*tmp <= '9')) || (*tmp == '*') || (*tmp == '#') || (*tmp == '?') || (*tmp == ',') ||
              (*tmp == 'P') || (*tmp == 'p'))) {
            return AT_FAILURE;
        }
        tmp++;
    }
    return AT_SUCCESS;
}

