        tftInfo->opTosMask = 1;

        if (atAuc2ul(g_atParaList[AT_CGTFT_TYPE_OF_SERVICE].para, (VOS_UINT16)(count - 1), &low) == AT_FAILURE) {
            return AT_FAILURE;
        }
