VOS_UINT32 AT_CheckDtmfKey(AT_ParseParaType *atPara)
{
    VOS_CHAR key;

    if (atPara->paraLen != 1) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    if (At_UpString(atPara->para, atPara->paraLen) != AT_SUCCESS) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    key = (VOS_CHAR)atPara->para[0];
    if (!(((key >= '0') && (key <= '9')) || (key == '*') || (key == '#') || (key == 'A') || (key == 'B') ||
          (key == 'C') || (key == 'D'))) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    return AT_SUCCESS;
}


