    if (rst == VOS_TRUE) {
        /* 指示当前用户的命令操作类型为设置命令 */
        g_atClientTab[indexNum].cmdCurrentOpt = AT_CMD_NETSCAN_SET;
        return AT_WAIT_ASYNC_RETURN;
    } else {
        return AT_ERROR;
    }
