    /* 检查是否已有处于OM模式的通道 */
    if (At_CheckDataState(AT_OM_DATA_MODE) != AT_SUCCESS) {
        return VOS_ERR;
    }

    At_SetMode(g_atOmIndex, AT_CMD_MODE, AT_NORMAL_MODE); /* 返回命令模式 */
