VOS_UINT32 AT_SetDamParaStub(VOS_UINT8 indexNum)
{
    TAF_PLMN_Id imsiPlmnList[16];
    VOS_UINT8   imsiPlmnNum;
    TAF_PLMN_Id damPlmnList[16];
    VOS_UINT8   damPlmnNum;
    VOS_UINT8   enableLteTrigPlmnSearchFlag;
    VOS_UINT8   addDamPlmnInDisabledPlmnList;
    VOS_UINT8   i;
    VOS_UINT8   paraIndex;
    VOS_UINT32  mcc;
    VOS_UINT32  mnc;
    VOS_UINT8 damActiveFlg;

    memset_s(&imsiPlmnList, sizeof(imsiPlmnList), 0x00, sizeof(imsiPlmnList));
    memset_s(&damPlmnList, sizeof(damPlmnList), 0x00, sizeof(damPlmnList));

    /*
     * 第一个参数为enable lte是否需要搜网，0为不需要；1为需要；
     * 第二个参数为DAM网络disable lte后是否加入禁止接入技术网络列表中，0为不需要；1为需要；
     * 第三个参数为imsi plmn list个数，如果个数为2，第四个参数和第五个参数为 imsi plmn id。
     * 第六个参数为DAM plmn list 个数，如果个数为2，第七个参数和第8个参数为dam plmn id
     */

    /* 获取ucDamActiveFlg */
    if (At_String2Hex(g_atParaList[0].para, g_atParaList[0].paraLen, &g_atParaList[0].paraValue) == AT_FAILURE) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    if ((g_atParaList[0].paraValue != 0) && (g_atParaList[0].paraValue != 1)) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    damActiveFlg = g_atParaList[0].paraValue;

    /* 获取ucEnableLteTrigPlmnSearchFlag */
    if (At_String2Hex(g_atParaList[1].para, g_atParaList[1].paraLen, &g_atParaList[1].paraValue) == AT_FAILURE) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    if ((g_atParaList[1].paraValue != 0) && (g_atParaList[1].paraValue != 1)) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    enableLteTrigPlmnSearchFlag = g_atParaList[1].paraValue;

    /* 获取ucAddDamPlmnInDisabledPlmnList */
    if (At_String2Hex(g_atParaList[AT_DAMCFGSTUB_DAMDISABLELTE].para,
                      g_atParaList[AT_DAMCFGSTUB_DAMDISABLELTE].paraLen,
                      &g_atParaList[AT_DAMCFGSTUB_DAMDISABLELTE].paraValue) == AT_FAILURE) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    if ((g_atParaList[AT_DAMCFGSTUB_DAMDISABLELTE].paraValue != 0) &&
        (g_atParaList[AT_DAMCFGSTUB_DAMDISABLELTE].paraValue != 1)) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    addDamPlmnInDisabledPlmnList = g_atParaList[AT_DAMCFGSTUB_DAMDISABLELTE].paraValue;

    /* 获取ucImsiPlmnNum */
    if (At_String2Hex(g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST].para, g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST].paraLen,
                      &g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST].paraValue) == AT_FAILURE) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    if (g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST].paraValue > 16) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    imsiPlmnNum = g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST].paraValue;
    for (i = 1; i <= imsiPlmnNum; i++) {
        /* Mcc */
        if (At_String2Hex(g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST + i].para, AT_MCC_LENGTH, &mcc) == AT_FAILURE) {
            return AT_CME_INCORRECT_PARAMETERS;
        }

        imsiPlmnList[i - 1].mcc = mcc;

        /* Mnc */
        if (At_String2Hex(&g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST + i].para[3],
                          g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST + i].paraLen - 3, &mnc) == AT_FAILURE) {
            return AT_CME_INCORRECT_PARAMETERS;
        }

        imsiPlmnList[i - 1].mnc = mnc;

        imsiPlmnList[i - 1].mcc |= 0xFFFFF000U;
        imsiPlmnList[i - 1].mnc |= (0xFFFFFFFFU << ((g_atParaList[AT_DAMCFGSTUB_DAMPLMNLIST + i].paraLen - 3) * 4));
    }

    /* ucDamPlmnNum */
    paraIndex = 3 + imsiPlmnNum + 1;
    if (At_String2Hex(g_atParaList[paraIndex].para, g_atParaList[paraIndex].paraLen,
                      &g_atParaList[paraIndex].paraValue) == AT_FAILURE) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    damPlmnNum = g_atParaList[paraIndex].paraValue;

    for (i = 1; i <= damPlmnNum; i++) {
        /* Mcc */
        if (At_String2Hex(g_atParaList[paraIndex + i].para, AT_MCC_LENGTH, &damPlmnList[i - 1].mcc) == AT_FAILURE) {
            return AT_CME_INCORRECT_PARAMETERS;
        }

        /* Mnc */
        if (At_String2Hex(&g_atParaList[paraIndex + i].para[3], g_atParaList[paraIndex + i].paraLen - 3,
                          &damPlmnList[i - 1].mnc) == AT_FAILURE) {
            return AT_CME_INCORRECT_PARAMETERS;
        }

        damPlmnList[i - 1].mcc |= 0xFFFFF000U;
        damPlmnList[i - 1].mnc |= (0xFFFFFFFFU << ((g_atParaList[paraIndex + i].paraLen - 3) * 4));
    }

    NAS_SetDamCfg(damActiveFlg, enableLteTrigPlmnSearchFlag, addDamPlmnInDisabledPlmnList, imsiPlmnNum, imsiPlmnList,
                  damPlmnNum, damPlmnList);
    return AT_OK;
}


