VOS_UINT32 AT_CommBoosterParaCheck(VOS_VOID)
{
    if (g_atParseCmd.cmdOptType != AT_CMD_OPT_SET_PARA_CMD) {
        return VOS_ERR;
    }

    if (g_atParaIndex != 4) {
        return VOS_ERR;
    }

    if ((g_atParaList[AT_COMMBOOSTER_RAT].paraLen == 0) || (g_atParaList[AT_COMMBOOSTER_LEN].paraLen == 0) ||
        (g_atParaList[AT_COMMBOOSTER_BS].paraLen  == 0)) {
        return VOS_ERR;
    }

    /* g_atParaList[1]记录字节数，g_atParaList[2]是字符个数，一个字节占2个字符，这里做一下校验 */
    if ((g_atParaList[AT_COMMBOOSTER_LEN].paraValue != g_atParaList[3].paraLen / 2) ||
        (g_atParaList[AT_COMMBOOSTER_BS].paraLen % 2 != 0)) {
        return VOS_ERR;
    }

    /* 字符的个数大于500都是错误参数 */
    if (g_atParaList[AT_COMMBOOSTER_BS].paraLen > AT_CMD_COMM_BOOSTER_BS_MAX_LENGTH) {
        return VOS_ERR;
    }

    return VOS_OK;
}


VOS_UINT32 AT_SetCommBoosterPara(VOS_UINT8 indexNum)
{
    AT_MTA_CommBoosterSetReq   commBoosterSet;
    AT_MTA_CommBoosterQueryReq commBoosterQuery;
    VOS_UINT32 result;

    if (AT_CommBoosterParaCheck() != VOS_OK) {
        return AT_CME_INCORRECT_PARAMETERS;
    }

    if (g_atParaList[0].paraValue == 1) {
        memset_s(&commBoosterSet, sizeof(commBoosterSet), 0x00, sizeof(commBoosterSet));
        commBoosterSet.pidIndex = (VOS_UINT16)g_atParaList[AT_COMMBOOSTER_RAT].paraValue;
        commBoosterSet.len      = g_atParaList[AT_COMMBOOSTER_LEN].paraValue;
        memset_s(commBoosterSet.date, AT_CMD_COMM_BOOSTER_BS_MAX_LENGTH, 0x00, sizeof(commBoosterSet.date));

        if (At_AsciiString2HexSimple(commBoosterSet.date, g_atParaList[AT_COMMBOOSTER_BS].para,
                                     g_atParaList[AT_COMMBOOSTER_BS].paraLen) == AT_FAILURE) {
            return AT_CME_INCORRECT_PARAMETERS;
        }

        result = AT_FillAndSndAppReqMsg(g_atClientTab[indexNum].clientId, 0, ID_AT_MTA_COMM_BOOSTER_SET_REQ,
                                        (VOS_UINT8 *)&commBoosterSet, sizeof(AT_MTA_CommBoosterSetReq),
                                        I0_UEPS_PID_MTA);
    } else {
        memset_s(&commBoosterQuery, sizeof(commBoosterQuery), 0x00, sizeof(commBoosterQuery));
        commBoosterQuery.pidIndex = (VOS_UINT16)g_atParaList[AT_COMMBOOSTER_RAT].paraValue;
        commBoosterQuery.len      = g_atParaList[AT_COMMBOOSTER_LEN].paraValue;
        memset_s(commBoosterQuery.date, AT_CMD_COMM_BOOSTER_BS_MAX_LENGTH, 0x00,
                 sizeof(commBoosterQuery.date));

        if (At_AsciiString2HexSimple(commBoosterQuery.date, g_atParaList[AT_COMMBOOSTER_BS].para,
                                     g_atParaList[AT_COMMBOOSTER_BS].paraLen) == AT_FAILURE) {
            return AT_CME_INCORRECT_PARAMETERS;
        }

        result = AT_FillAndSndAppReqMsg(g_atClientTab[indexNum].clientId, 0, ID_AT_MTA_COMM_BOOSTER_QRY_REQ,
                                        (VOS_UINT8 *)&commBoosterQuery, sizeof(AT_MTA_CommBoosterQueryReq),
                                        I0_UEPS_PID_MTA);
    }

    if (result != TAF_SUCCESS) {
        return AT_ERROR;
    }

    if (g_atParaList[0].paraValue == 1) {
        g_atClientTab[indexNum].cmdCurrentOpt = AT_CMD_COMM_BOOSTER_SET;
    } else {
        g_atClientTab[indexNum].cmdCurrentOpt = AT_CMD_COMM_BOOSTER_QRY;
    }

    return AT_WAIT_ASYNC_RETURN;
}


